/* Main Colors - Add your primary, accent and text colors. You can also use hex color codes i.e #FF0000 */

:root{
  --main-color:#231F20;
  --accent-color:#B1875D;
  --third-color: #ffffff;
  --header: rgba(255, 255, 255, 0.50); 
  --text-color: #000000;
}

/* The complicated stuff you don’t need to touch. */

.btn.btn {
  background:var(--accent-color);
  color:var(--third-color); 
  border-radius:0px;
  border: 0px solid var(--accent-color);
  box-shadow: 0px 17px 10px -10px rgba(0,0,0,0.4);
}

.btn.btn:active, .btn.btn:hover {
  background: #C2B59B;
  color:var(--third-color);
   border-radius:0px;
  border: 0px solid var(--accent-color);
  box-shadow:0px 17px 10px -10px rgba(0,0,0,0.4);
  transition: .2s ease;
}


li::marker {
  color:var(--accent-color); 
}

hr {
 border-bottom: 1.5px solid var(--accent-color);
}

/*/ Header & Slides /*/
#lesson-header {
  background:var(--header);
  box-shadow:0px 7px 30px -15px rgba(0,0,0,0.4); 
}
#lesson-header-title-logo {
    width: 130px;
    height: auto;
}
#lesson-header-nav {
  background: transparent;
  color:var(--text-color);
 margin-right: -2em;
 width: 120px; 
}
#lesson-header-nav-menu-btn {
  border-left: none;
}
#lesson-header-title-content {
  border-bottom: none;
  color:var(--text-color); 
}
#indicator { 
  background: var(--third-color);
  color:var(--accent-color);
}
.icon-menu:before {
    content: "☰";
}
.icon-menu, .icon-menu:before {
}

#slides {
  border-bottom: 0px solid var(--accent-color);
}

.game-slide .slide-footer {
   border-top: none;
}

.slide-footer .btn-muted {
  text-transform: uppercase;
}

/* OPTIONAL: Bottom prompt highlight color*/
.slide-footer .slide-footer-prompt-content {
    background-color:0;
color:var(--text-color);
line-height: 1.5;


}


/*OPTIONAL: Table formatting */
/*#<name of table> within the lesson */
#table td, #table th {
  border: 1px solid #ddd;
  padding: 8px;
}
/*/ TEMPLATES /*/

ul.md-list {
     padding: 0 0.1em; }

/* BACKGROUND OF ZOOMED IMAGES */
.pswp__item {
    background:var(--accent-color);
}

.pswp__caption {
  background: var(--text-color);
  color:var(--accent-color);
}

/* IMAGE SLIDER */
.slide-image-slider .style-dark .caption-content {
  background:var(--main-color);
  color:var(--third-color);
  border-radius:0px;
}

.slide-image-slider .style-light .caption-content {  
  background:var(--accent-color);
  color:var(--third-color);
  border-radius:0px;
}

.slide-image-slider .btn-up, .slide-text-sequence .btn-up /* button up */{
  top: 1.75em; }

.slide-image-slider .caption.position-bottom {
  bottom: 3.75em; }

/* IMAGE GALLERY */
.slide-image-gallery .pagination .dot {
  background:var(--btn-color);
  color:var(--btn-color);
} 

.slide-image-gallery .pagination .dot.active {
  color:var(--accent-color);
} 

/* IMAGE COLLECTION */
.slide-image-collection .tappy{ 
  transition: transform .5s ease;
}

.slide-image-collection .tappy:hover {
 transform: scale(1.1);
}

/* TEXT SEQUENCE */
.text-center ul, .text-center ol {
    display: table;
    margin-left: 0.5em; 
}

/* REVEAL */
.slide-reveal .btn-reveal-title.flippable {
  background:var(--accent-color);
  color: var(--text-color); 
} 

.slide-reveal .btn-reveal.active .btn-reveal-desc.flippable {
  background:var(--main-color);
  color: var(--text-color); 
}

.slide-reveal .btn-reveal-desc, .slide-reveal .btn-reveal-title {
  pointer-events:none;
}

/* EXPANDABLE LIST */
.slide-expandable-list .item.active .item-button {
  background:var(--third-color);  
  color: var(--accent-color); 
}

/* IMAGE WAYPOINTS */
.slide-image-waypoints .prev-wp, .slide-image-waypoints .next-wp{ 
  background:var(--accent-color);
  color: var(--third-color);
}

.slide-list ::marker {
  color:var(--accent-color); 
}

/* SPEECH BUBBLE */
.slide-speech-bubble .quote.quote-dk .quote-bg, .quote.quote-dk {
   background:var(--main-color);  
   color: var(--text-color); 
   opacity: 1;
}

.slide-speech-bubble .quote.pull-left.quote-dk:after {
    border-color: var(--main-color) transparent transparent;
}

/* COMPARISON */
.slide-comparison .slider-knob {
    background-color:var(--accent-color);
}

.slide-comparison .slider-knob:before, .slide-comparison .slider-knob:after {
   background-color:var(--accent-color);
}

/* TABLE */
.slide-table .table-row:nth-child(odd) {
    border-radius:3px;
    background: var(--accent-color);
    color: var(--text-color);
}

.slide-table .table-row:nth-child(even) {
    border-radius:3px;
    background:var(--accent-color);
    color: var(--text-color); 
}

/*/Interactions/*/
#text-input-view #text-input-dismiss-btn {
  color:var(--main-color);
} 

#text-input-view #text-input-container-top {
  color:var(--main-color);
}

.slide-chat .message--question {
  background:var(--main-color);
  color:var(--text-color);
}

.slide-chat.show-msg-true .selectable {
  background:var(--btn-color);
  color:var(--text-color);
}

.slide-chat.show-msg-true .selectable.active {
  background: var(--main-color);
  color:var(--text-color);
}

.slide-chat.state-complete .chat-msg.incorrect {
  background:var(--main-color);
  color:var(--text-color);
}

.slide-chat.state-complete .chat-msg.correct {
  background:var(--accent-color);
  color:var(--text-color);
}

.slide-chat .message--question:after {
  border-color:var(--main-color) transparent transparent;
}

.slide-chat .message--answer:after {
      border-color: white transparent transparent;
    border-width: 0px;
}

.slide-circle-the-answer .circle-box.active .circle-box-text, .text-dark .slide-circle-the-answer .circle-box.active .circle-box-text {
  background:var(--accent-color);
  color:var(--text-color);
}

.slide-multiple-choice-game .btn.btn-solid {
  background:var(--accent-color);
  color:var(--third-color);
}

.slide-multiple-choice-game .selectable.active .btn {
  background:var(--third-color);
  color:var(--accent-color);
}

.slide-multiple-choice-game.state-complete .selectable.incorrect .btn {
  background:var(--main-color);
  color:var(--text-color);
}

.slide-multiple-choice-game.state-complete .selectable.correct .btn {
  background:var(--accent-color);
  color:var(--third-color);
}

.slide-connect .connect-box {
  background:var(--btn-color);
  color:var(--text-color);
}

.slide-connect .connect-box.complete {
  background:var(--accent-color);
  color:var(--text-color);
}

.slide-game-true-or-false .game-statement {
  background:var(--accent-color);
  color:var(--text-color);
}

.slide-game-image-word-match .btn-word {
  background:var(--main-color);
  color:var(--text-color);
}

.slide-game-image-word-match .btn-word.__active {
  background:var(--accent-color);
  color:var(--text-color);
}

.slide-slider .slider-bar, .slide-slider-survey .slider-bar {
  background:var(--main-color);
}

.slide-slider.active .slider-knob-input, .active.slide-slider-survey .slider-knob-input {
  background:var(--accent-color);
}

.slide-free-text-survey .btn-prompt, .slide-free-text-contribution .btn-prompt {
  border-radius:50% !important;
}

.slide-carousel .pagination {
    color: var(--accent-color);
}

.slide-drag-to-match .draggy {
  transition: transform 300ms;
  background: var(--accent-color);
  color: var(--main-color);
}

.slide-drag-to-match .droppy {
  transition: transform 300ms;
  background: var(--main-color);
  color: var(--text-color);
}


ul.md-list {
     padding: 0 0.2em; }
